

int addChar(char *s, char c) {
  char *originalPointPos = s;
  while(*s++);
  if ((s - originalPointPos) < 512) /* Plz modify if you have another number of chars in your array */ {

  *(s - 1) = c;
  //printf("s:%d\n", s);
  //printf("origpointpos:%d\n", originalPointPos);
  //printf("*s:%d\n", *s);
  //printf("*origpointpos:d\n", *originalPointPos);
  *s = '\0';
  return 0;
  } else {
    return 1;
  }
}

